SC1_by_id.map:
function (doc) {
  if (doc.type == "movie") {
      emit(doc.movie_id, {idmovies: doc.movie_id, title: doc.title, year: doc.year, keywords: doc.keywords, genres: doc.genres, series: doc.series});
  } else if (doc.type == "actor") {
    for (var ai in doc.acted_in) {
      emit(ai.idmovies, {idactors: doc.idactors, fname: doc.fname, lname: doc.lname, gender: doc.gender, character: ai.character});
    }
  }
}

SC2_by_id.map:
function (doc) {
    if (doc.type == "actor") {
        emit(doc.idactors, {idactors: doc.idactors, fname: doc.fname, lname: doc.lname, gender: doc.gender});
        for (var ai in doc.acted_in) {
            emit(doc.idactors, ai);
        }
    }
}

SC3_by_id.map:
function (doc) {
    if (doc.type == "actor") {
        emit(doc.idactors, {idactors: doc.idactors, fname: doc.fname, lname: doc.lname, acted_in_count: Object.keys(doc.acted_in).length()});
    }
}

SC4_by_id.map:
function (doc) {
    if(doc.type == "movie"){
        emit([doc.genre, doc.year], {idmovies: doc.movie_id, title: doc.title, year: doc.year});
    }
}
